package com.leetcode;

import java.util.ArrayList;
import java.util.List;

public class BullsAndCows {


    /*.
    * ""
""
    *
    * */
    public String getHint(String secret, String guess) {

        String result = "";

        int bullPointer = 0;

        int bullCount = 0;

        if (secret.equals(guess)) {
            return result + secret.length() + "A" + "0B";
        }

        List<Integer> bullList = new ArrayList<>();
        while (bullPointer < secret.length()) {
            if (secret.charAt(bullPointer) == guess.charAt(bullPointer)) {
                bullList.add(bullPointer);
                bullCount++;
            }
            bullPointer++;
        }
        result += bullCount + "A";
        //System.out.println("--result = "+result);


        int cowCount = 0;
        //System.out.println("------"+bullList);
        List<Integer> visitedIndex = new ArrayList<>();

        for (int i = 0; i < secret.length(); i++) {
            if (bullList.contains(i)) continue;

            for (int j = 0; j < guess.length(); j++) {

                if (bullList.contains(j)) continue;

                if (!visitedIndex.contains(j) && secret.charAt(i) == guess.charAt(j)) {
                    visitedIndex.add(j);
                    cowCount++;
                    break;


                }
            }
        }

        result += cowCount + "B";


        return result;

    }
}
