package com.leetcode;

public class LongestSubstringWithAtleastKRepeatingCharactersBruteForce {
    public static void main(String[] args) {
        int res = new LongestSubstringWithAtleastKRepeatingCharactersBruteForce().longestSubstring(""
                , 301);
        System.out.println("res = " + res);
    }

    public int longestSubstring(String s, int k) {

        int[] freq = new int[26];

        for (int i = 0; i < s.length(); i++) {
            char c = s.charAt(i);
            freq[c - 'a']++;
        }

        int maxLength = 0;

        int left = 0;
        int right = 0;

        while (left < s.length()) {
            while (right < s.length()) {
                String subString = s.substring(left, right + 1);

                boolean res = isValid(subString, k);
                if (res) {
                    maxLength = Math.max(maxLength, subString.length());
                }
                right++;

            }
            left++;
            right = left;
        }

        return maxLength;


    }

    private boolean isValid(String str, int K) {

        int[] freq = new int[26];

        for (int i = 0; i < str.length(); i++) {
            char c = str.charAt(i);
            freq[c - 'a']++;
        }

        for (int i = 0; i < freq.length; i++) {
            if (freq[i] > 0 && freq[i] < K) {
                return false;
            }
        }

        return true;

    }


}
